    private void actualizarProfesorBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_actualizarProfesorBtnActionPerformed

        String p_dia = p_DiaTxt.getText();
        String p_mora = p_MoraTxt.getText();
        String p_cantidad = p_CantidadTxt.getText();

        if (p_DiaTxt.getText().equals("") || p_MoraTxt.getText().equals("") || p_CantidadTxt.getText().equals("")) {

            JOptionPane.showMessageDialog(null, "Error, no puedes dejar campos vacíos...");
            return;
        }
        
        if (!Utilidades.verificarNumero(p_dia) || !Utilidades.verificarNumero(p_mora) || !Utilidades.verificarNumero(p_cantidad)) {
            
            JOptionPane.showMessageDialog(null, "Error, has ingresado un dato que no es númerico.....");
            return;
        }

        if (Integer.parseInt(p_dia) <= 0 || Integer.parseInt(p_mora) <= 0 || Integer.parseInt(p_cantidad) <= 0) {

            JOptionPane.showMessageDialog(null, "Error, los campos de día y mora deben de ser mayores que 0...");
            return;
        }

        try {

            AdminControlador ad = new AdminControlador();
            Administracion admin = new Administracion();

            admin.setDiaProfesor(Integer.parseInt(p_dia));
            admin.setMoraProfesor(Integer.parseInt(p_mora));
            admin.setMaterialProfesor(Integer.parseInt(p_cantidad));
            ad.actualizarAdministracionProfesor(admin);

            JOptionPane.showMessageDialog(null, "Los campos del profesor han sido modificado con exito...");

            p_DiaTxt.setText("");
            p_MoraTxt.setText("");
            p_CantidadTxt.setText("");

        } catch (Exception e) {
            System.out.println(e.getMessage());
        }
    }//GEN-LAST:event_actualizarProfesorBtnActionPerformed

    private void actualizarEstudianteBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_actualizarEstudianteBtnActionPerformed

        String e_dia = e_DiaTxt.getText();
        String e_mora = e_MoraTxt.getText();
        String e_cantidad = e_CantidadTxt.getText();

        if (e_DiaTxt.getText().equals("") || e_MoraTxt.getText().equals("") || e_CantidadTxt.getText().equals("")) {

            JOptionPane.showMessageDialog(null, "Error, no puedes dejar campos vacíos...");
            return;
        }
        
        if (!Utilidades.verificarNumero(e_dia) || !Utilidades.verificarNumero(e_mora) || !Utilidades.verificarNumero(e_cantidad)) {
            
            JOptionPane.showMessageDialog(null, "Error, has ingresado un dato que no es númerico.....");
            return;
        }
        
        if (Integer.parseInt(e_dia) <= 0 || Integer.parseInt(e_mora) <= 0 || Integer.parseInt(e_cantidad) <= 0) {

            JOptionPane.showMessageDialog(null, "Error, los campos de día y mora deben de ser mayores que 0...");
            return;
        }

        try {

            AdminControlador ad = new AdminControlador();
            Administracion admin = new Administracion();

            admin.setDiaEstudiante(Integer.parseInt(e_dia));
            admin.setMoraEstudiante(Integer.parseInt(e_mora));
            admin.setMaterialEstudiante(Integer.parseInt(e_cantidad));
            ad.actualizarAdministracionEstudiante(admin);

            JOptionPane.showMessageDialog(null, "Los campos del estudiante han sido modificado con exito...");

            e_DiaTxt.setText("");
            e_MoraTxt.setText("");
            e_CantidadTxt.setText("");

        } catch (Exception e) {
            System.out.println(e.getMessage());
        }
    }//GEN-LAST:event_actualizarEstudianteBtnActionPerformed

