    public void cargarDatos() {

        AdminControlador ac = new AdminControlador();
        Administracion admin = ac.consultarAdmin();

        e_DiaTxt.setText(String.valueOf(admin.getDiaEstudiante()));
        e_MoraTxt.setText(String.valueOf(admin.getMoraEstudiante()));
        e_CantidadTxt.setText(String.valueOf(admin.getMaterialEstudiante()));

        p_DiaTxt.setText(String.valueOf(admin.getDiaProfesor()));
        p_MoraTxt.setText(String.valueOf(admin.getMoraProfesor()));
        p_CantidadTxt.setText(String.valueOf(admin.getMaterialProfesor()));

    }

